queries.text:
let plant_info = lines;
let adi_pressures=
silver
| where timestamp between(_startTime .. _endTime) and device == 'adi' and category == 'whc-pressure' and lineUpl == _lineupl
| project utctimestamp=timestamp, lineupl=lineUpl, location, productionline=productionLine, tagname=strcat('channel ', tostring(data.channel)), value=toreal(data.pressure);
let procmon_pressures=
silver
| where timestamp between(_startTime .. _endTime) and device == 'procmon' and category == 'machine-pressures' and lineUpl == _lineupl
| project utctimestamp=timestamp, lineupl=lineUpl, location, productionline=productionLine, tagname=tostring(data.tagname), value=toreal(data.value);
procmon_pressures
| union adi_pressures
| lookup lines on $left.lineupl==$right.lineUPL
| project utctimestamp, localtimestamp=datetime_utc_to_local(utctimestamp, timeZone), lineupl, location, productionline, tagname, value
